/*
 * @Description: Description
 * @Author: ZHANG ZHEN
 * @Date: 2021-05-18 23:38:41
 * @LastEditors: ZHANG ZHEN
 * @LastEditTime: 2021-05-19 22:40:48
 */
import React, { Component } from 'react';
import { observer, inject } from 'mobx-react';

@observer
//inject是吧根容器中的元素映射到子元素中
@inject('productsStore')
class products extends Component {

    render() {
        const { productsStore } = this.props;

        return (
            <div>
                <h2>Products</h2>
               <ul>
               {
                    productsStore['all'].map(item => (
                        <li  key={ item['id'] }>
                            { item['title'] } - { item['price'] } * { item['inventory'] }
                            <br/>
                            <button onClick={ this.props.addToCart(item) }>Add to cart</button>
                        </li>
                    ))
                }
               </ul>
            </div>
        );
    }
    componentDidMount () {
        this.props.productsStore.getAllProducts();//加载所有的商品
    } 
}

export default products;
